function generate_frequencies($filename){
	$myfile = fopen($filename, "r") or die("No es posible abrir el archivo\n");


	$letter_count = array_fill(0,26,0);
	$num_char = 0;

	$text;
	while(!feof($myfile)) {
	  	$text = strtolower(transliterateString(fgets(($myfile))));
	  	break;
	}  

  	for ($i=0; $i<strlen($text); $i++) {
  		$c = $text[$i];
  		if($c>= 'a' && $c <= 'z'){
  			$letter_count[ord($c) - ord('a')]++;
  			$num_char++;
  		}
  	}
	
	fclose($myfile);

	$frequencies = array();

	for ($i=0; $i != 26 ; $i++) { 
		array_push($frequencies, $letter_count[$i]/($num_char*1.0));
	}

	return $frequencies;
}



//main

if(count($argv) != 2){
	die("Especifica la ruta del archivo!\n");
}

$fichero_entrenamiento = (dirname(__FILE__) . "/language.net");
if (!is_file($fichero_entrenamiento))
 die("No ha sido creado el documento");

$rna = fann_create_from_file($fichero_entrenamiento);
if (!$rna)
 die("No se pudo crear ANN");

$entrada = generate_frequencies($argv[1]);
$output = fann_run($rna, $entrada);

echo "Español: ".$output[3]."\n";
echo "English: ".$output[2]."\n";
echo "Deutsch: ".$output[1]."\n";
echo "Italiano: ".$output[0]."\n";

fann_destroy($rna);