function generate_frequencies($filename){
	$myfile = fopen($filename, "r") or die("No es posible abrir el archivo\n");


	$letter_count = array_fill(0,26,0);
	$num_char = 0;

	$text;
	while(!feof($myfile)) {
	  	$text = strtolower(transliterateString(fgets(($myfile))));
	  	break;
	}  

  	for ($i=0; $i<strlen($text); $i++) {
  		$c = $text[$i];
  		if($c>= 'a' && $c <= 'z'){
  			$letter_count[ord($c) - ord('a')]++;
  			$num_char++;
  		}
  	}
	
	fclose($myfile);

	$frequencies = array();

	for ($i=0; $i != 26 ; $i++) { 
		array_push($frequencies, $letter_count[$i]/($num_char*1.0));
	}

	return $frequencies;
}



//main

if(count($argv) != 3){
	die("Necesita de 2 argumentos 1: nombre archivo; 2: idioma e.g. Español = 0; Inglés = 1; Aleman: 2; Italiano:3;\n");
}

if(intval($argv[2]) > 3 || intval($argv[2]<0)){
	die("El segundo parametro debe de estar entre 0 y 2 e. g.\nEspañol = 0\nInglés = 1\nAleman: 2\nItaliano = 3");
}

$freqFile = fopen('frequencies.data','c+');
$freqFileArray = array();

if(!filesize('frequencies.data')){
	fwrite($freqFile, "1 26 4".PHP_EOL.PHP_EOL);
}else{
	while(!feof($freqFile)) {
		array_push($freqFileArray, fgetc($freqFile));
	}
	fclose($freqFile);
	if($freqFileArray[1] == ' '){
		$freqFileArray[0]++;	
	}else{
		$int = intval($freqFileArray[0].$freqFileArray[1]);
		$int++;
		$intstr = strval($int);
		$freqFileArray[0] = $intstr[0];
		$freqFileArray[1] = $intstr[1];

	}
	$freqFile = fopen('frequencies.data','w+');
	foreach ($freqFileArray as $key => $value) {
		fwrite($freqFile, $value);
	}
	fclose($freqFile);
}

$freqFile = fopen('frequencies.data', 'a');

$frequencies = generate_frequencies($argv[1]);

for ($i=0; $i != 26 ; $i++) { 
	fwrite($freqFile, $frequencies[$i] . ' ');
}

fwrite($freqFile,PHP_EOL.PHP_EOL);
if($argv[2] == "0"){
	fwrite($freqFile, '-1 -1 -1 1'.PHP_EOL); 	
}else if($argv[2] == "1"){
	fwrite($freqFile,'-1 -1 1 -1'.PHP_EOL);
}else if($argv[2] == "2"){
	fwrite($freqFile, '-1 1 -1 -1'.PHP_EOL);
}else if($argv[2] == "3"){
	fwrite($freqFile, '1 -1 -1 -1'.PHP_EOL);
}

fwrite($freqFile, PHP_EOL);	

fclose($freqFile);

echo "Agregada la frase a frequencies.data\n";






